import React from "react";
import { ListItem } from "../List";
// import { Row, Col } from "../Grid";
import "./style.css";
import Button from 'react-bootstrap/Button'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import ResponsiveEmbed from 'react-bootstrap/ResponsiveEmbed'
import Card from 'react-bootstrap/Card';

function Book({ title, subtitle, authors, link, description, image, Button }) {
  return (
    <ListItem>
      <Card border="light">
      <Row >
        <Col md="8">
          <Card.Header className="font-italic" >
            <Card.Link href={link}>
          {title}    
            </Card.Link>

          </Card.Header>

          {subtitle && <Card.Title className="font-italic">{subtitle}</Card.Title>}
        </Col>
        <Col size="md-4">
          <div className="btn-container">
            <a className="btn btn-light" target="_blank" rel="noopener noreferrer" href={link}>
              View
            </a>
            <Button />
          </div>
        </Col>
      </Row>
      <Row>
        <Col size="md-6">
          <p className="font-italic small">Written by {authors}</p>
        </Col>
      </Row>
      <Row>
        <Col md="4">
        <ResponsiveEmbed aspect="a4by3" style={{ width: 200, height: 'auto' }}>
          <img className="img-thumbnail w-100" src={image} alt={title} />
          </ResponsiveEmbed>
        </Col>
        <Col size="12 sm-8 md-10">
          <Card.Text>{description}</Card.Text>
        </Col>
      </Row>
      </Card>
    </ListItem>
  );
}

export default Book;

