import React, { Component } from "react";
import Jumbotron from "../components/Jumbotron";

// import Form from "../components/Form";
import Book from "../components/Book";
import Footer from "../components/Footer";
import API from "../utils/API";
// import { Col, Row, Container } from "../components/Grid";
import { List } from "../components/List";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form'

class Home extends Component {
  state = {
    books: [],
    q: "",
    message: "Search For A Book To Begin!"
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  getBooks = () => {
    API.getBooks(this.state.q)
      .then(res =>
        this.setState({
          books: res.data
        })
      )
      .catch(() =>
        this.setState({
          books: [],
          message: "No New Books Found, Try a Different Query"
        })
      );
  };

  handleFormSubmit = event => {
    event.preventDefault();
    this.getBooks();
  };

  handleBookSave = id => {
    const book = this.state.books.find(book => book.id === id);

    API.saveBook({
      googleId: book.id,
      title: book.volumeInfo.title,
      subtitle: book.volumeInfo.subtitle,
      link: book.volumeInfo.infoLink,
      authors: book.volumeInfo.authors,
      description: book.volumeInfo.description,
      image: book.volumeInfo.imageLinks.thumbnail
    }).then(() => this.getBooks());
  };

  render() {
    return (
      <div>


        <Container>
          <Row>
            <Col size="md-12">
              <Card >
                <Card.Header className="text-center">Search for a Book</Card.Header>
                <Card.Body>

                  <Card title="Book Search" icon="far fa-book">
                    <Card.Body>
                    <Col md="6" className="justify-content-md-center">
                    <Form>
                      <Form.Group controlId="formBasicEmail">
                        <Form.Label>Book you want to search?</Form.Label>
                        <Form.Control 
                          className="form-control"
                          id="Title"
                          type="text"
                          value={this.state.q}
                          placeholder="Example: Lord of the Rings"
                          name="q"
                          onChange={this.handleInputChange}
                          required
                         />

                      </Form.Group>

                      <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder="Password" />
                      </Form.Group>

                      <Button variant="primary" type="submit">
                        Submit</Button>
                    </Form>
                    </Col>
                    </Card.Body>
                    <Form>
                      <div className="form-group">
                        <label htmlFor="Query">
                          <strong>Book</strong>
                        </label>
                        <input
                          className="form-control"
                          id="Title"
                          type="text"
                          value={this.state.q}
                          placeholder="Ready Player One"
                          name="q"
                          onChange={this.handleInputChange}
                          required
                        />
                      </div>
                      <div className="pull-right">
                        <button
                          onClick={this.handleFormSubmit}
                          type="submit"
                          className="btn btn-lg btn-danger float-right" >Search</button>
                      </div>
                    </Form>


                  </Card>

                </Card.Body>
              </Card>
            </Col>
          </Row>





          <Row>
            <Col size="md-12">
              <Card>


                <Card title="Results">
                  {this.state.books.length ? (
                    <List>
                      {this.state.books.map(book => (
                        <Book
                          key={book.id}
                          title={book.volumeInfo.title}
                          subtitle={book.volumeInfo.subtitle}
                          link={book.volumeInfo.infoLink}
                          authors={book.volumeInfo.authors.join(", ")}
                          description={book.volumeInfo.description}
                          image={book.volumeInfo.imageLinks.thumbnail}
                          Button={() => (
                            <button
                              onClick={() => this.handleBookSave(book.id)}
                              className="btn btn-primary ml-2"
                            >
                              Save
                        </button>
                          )}
                        />
                      ))}
                    </List>
                  ) : (
                      <Card>
                        <Card.Header></Card.Header>
                        <Card.Body>

                          <h2 className="text-center">{this.state.message}</h2>

                        </Card.Body>
                      </Card>
                    )
                  }
                </Card>



              </Card>
            </Col>
          </Row>
          <Row>
            <Col>
              <Footer />
            </Col>

          </Row>
        </Container>


      </div>
    );
  }
}

export default Home;
